function
tar_file($kc, $Uf)
{
    $L = pack("a100a8a8a8a12a12", $kc, 644, 0, 0, decoct($Uf->size), decoct(time()));
    $La = 8 * 32;
    for ($t = 0; $t < strlen($L); $t++) $La += ord($L[$t]);
    $L .= sprintf("%06o", $La) . "\0 ";
    echo $L, str_repeat("\0", 512 - strlen($L));
    $Uf->send();
    echo
    str_repeat("\0", 511 - ($Uf->size + 511) % 512);
}

function
ini_bytes($Mc)
{
    $X = ini_get($Mc);
    switch (strtolower(substr($X, -1))) {
        case'g':
            $X *= 1024;
        case'm':
            $X *= 1024;
        case'k':
            $X *= 1024;
    }
    return $X;
}

