class
TmpFile
{
    var $handler;
    var $size;

    function
    TmpFile()
    {
        $this->handler = tmpfile();
    }

    function
    write($cb)
    {
        $this->size += strlen($cb);
        fwrite($this->handler, $cb);
    }

    function
    send()
    {
        fseek($this->handler, 0);
        fpassthru($this->handler);
        fclose($this->handler);
    }
}

