function
tar_file($nc, $gg)
{
    $K = pack("a100a8a8a8a12a12", $nc, 644, 0, 0, decoct($gg->size), decoct(time()));
    $Na = 8 * 32;
    for ($t = 0; $t < strlen($K); $t++) $Na += ord($K[$t]);
    $K .= sprintf("%06o", $Na) . "\0 ";
    echo $K, str_repeat("\0", 512 - strlen($K));
    $gg->send();
    echo
    str_repeat("\0", 511 - ($gg->size + 511) % 512);
}

function
ini_bytes($Pc)
{
    $X = ini_get($Pc);
    switch (strtolower(substr($X, -1))) {
        case'g':
            $X *= 1024;
        case'm':
            $X *= 1024;
        case'k':
            $X *= 1024;
    }
    return $X;
}

