class
TmpFile
{
    var $handler;
    var $size;

    function
    TmpFile()
    {
        $this->handler = tmpfile();
    }

    function
    write($eb)
    {
        $this->size += strlen($eb);
        fwrite($this->handler, $eb);
    }

    function
    send()
    {
        fseek($this->handler, 0);
        fpassthru($this->handler);
        fclose($this->handler);
    }
}

